package com.icoder0.websocket.core.model;import com.alibaba.fastjson.annotation.JSONField;import com.icoder0.websocket.core.exception.WsBusiCode;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;/** * @author bofa1ex * @since 2020/6/30 */@Data@Builder(access = AccessLevel.PRIVATE)public class WsOutboundBean<T> {    /* 消息序号 */    @JSONField(defaultValue = "0")    private Long seq;    private String code;    private String message;    private T body;    public static WsOutboundBeanBodyBuilder status(WsBusiCode code) {        return new WsOutboundBeanBodyBuilder(code);    }    public static WsOutboundBeanBodyBuilder ok() {        return new WsOutboundBeanBodyBuilder(WsBusiCode.OK);    }    public static <T> WsOutboundBean<T> ok(T body) {        return WsOutboundBean.<T>builder()                .body(body)                .code(WsBusiCode.OK.getCode())                .build();    }    public static class WsOutboundBeanBodyBuilder {        private final WsBusiCode code;        public WsOutboundBeanBodyBuilder(WsBusiCode code) {            this.code = code;        }        public <T> WsOutboundBean<T> body(T body) {            return WsOutboundBean.<T>builder()                    .code(code.getCode())                    .body(body)                    .build();        }        public <T> WsOutboundBean<T> message(String message) {            return WsOutboundBean.<T>builder()                    .code(code.getCode())                    .message(String.format(code.getZhMsg(), message))                    .build();        }    }}