package com.icoder0.websocket.core.model;import com.alibaba.fastjson.annotation.JSONField;import com.google.common.collect.ImmutableMap;import com.icoder0.websocket.core.exception.WsBusiCode;import lombok.AccessLevel;import lombok.Builder;import lombok.Data;import java.util.Map;/** * @author bofa1ex * @since 2020/6/30 */@Data@Builder(access = AccessLevel.PRIVATE)public class WsOutboundBean {    /* 消息序号 */    @JSONField(defaultValue = "0")    private Long seq;    @JSONField(ordinal = 1)    private Map<String, Object> result;    public static WsOutboundBeanBodyBuilder status(WsBusiCode code) {        return new WsOutboundBeanBodyBuilder(code);    }    public static WsOutboundBeanBodyBuilder ok() {        return new WsOutboundBeanBodyBuilder(WsBusiCode.OK);    }    public static WsOutboundBean ok(Object body) {        return WsOutboundBean.builder().result(WsBusiCode.mapper(WsBusiCode.OK, body)).build();    }    public static class WsOutboundBeanBodyBuilder {        private WsBusiCode code;        public WsOutboundBeanBodyBuilder(WsBusiCode code) {            this.code = code;        }        public WsOutboundBean body(Object body) {            return WsOutboundBean.builder().result(                    WsBusiCode.mapper(code, body)            ).build();        }        public WsOutboundBean message(String message) {            return WsOutboundBean.builder().result(                    WsBusiCode.mapper(code, message)            ).build();        }    }}